import solution


def test() -> str:
    s = solution.Solution()

    input1 = ["1", "1010", ""]
    input2 = ["11", "1011", ""]

    expectations = ["100", "10101", ""]

    if (len(input1) != len(expectations)):
        return "Test is invalid"

    for i in range(len(expectations)):
        assert (1 <= len(input1[i]) and len(input1[i]) <= 10**4)
        assert (1 <= len(input2[i]) and len(input2[i]) <= 10**4)

        if len(input1[i]) > 1:
            assert (input1[i][0] != "0")

        if len(input2[i]) > 1:
            assert (input2[i][0] != "0")

        for j in range(len(input1[i])):
            assert (input1[i][j] == "0" or input1[i][j] == "1")

        for j in range(len(input2[i])):
            assert (input2[i][j] == "0" or input2[i][j] == "1")

        result = s.addBinary(input1[i], input2[i])
        msg = (f"bad result for {input1[i]} and {input2[i]}: got {
            result} expected {expectations[i]}")

        if len(result) != len(expectations[i]):
            return msg

        for j in range(len(result)):
            if result[j] != expectations[i][j]:
                return msg

    return "PASS"


if __name__ == '__main__':
    print(test())
